import UIKit

public final class SpiralSpinner: UIView {
  
  private var color: UIColor
  
  init(frame: CGRect = .zero, color: UIColor = Colors.main) {
    self.color = color
    
    super.init(frame: frame)
    
    isOpaque = false
    backgroundColor = .clear
    heightAnchor.constraint(equalTo: widthAnchor, multiplier: 1/1).isActive = true
  }
  
  required init?(coder: NSCoder) {
    fatalError("init(coder:) has not been implemented")
  }
  
  // MARK: - Destructor
  deinit {
#if DEBUG
    print("\(String(describing: type(of: self))).\(#function)")
#endif
  }
  
  public func setColor(_ color: UIColor) {
    self.color = color
    setNeedsDisplay()
  }
  
  public func start(duration: TimeInterval) {
    startRotating(duration: duration, clockwise: true)
  }
  
  public func stop() {
    stopRotating()
  }
  
  public override func draw(_ rect: CGRect) {
    SpinnerStyleKit.drawSpiralDarkThemeCenterTest4Canvas(frame: rect, resizing: .aspectFit, color: color)
  }
}

